package chat;import javax.swing.*;import java.awt.*;public class Login extends JFrame {    private JLabel jLabel;    private JTextArea login;    private JButton enterLogin;    public Login() {        setLayout(new BorderLayout());        jLabel = new JLabel("Write your login:  (max 15 symboluw)");        login = new JTextArea();        enterLogin = new JButton("zaloguj");        enterLogin.addActionListener(e -> {            if (login.getText().length() <= 30 && login.getText().length() > 3) {                new Thread(() -> new Client(login.getText())).start();                dispose();            }        });        getContentPane().add(jLabel, BorderLayout.NORTH);        getContentPane().add(login, BorderLayout.CENTER);        getContentPane().add(enterLogin, BorderLayout.SOUTH);        setDefaultCloseOperation(DISPOSE_ON_CLOSE);        setPreferredSize(new Dimension(350, 350));        pack();        setVisible(true);    }}