package chat;import javax.swing.*;import java.awt.*;public class MainWindow extends JFrame{    private JTextArea glownaArea;    private JTextArea malaArea;    private JButton buttonWysli;    private JPanel panelAreaButton;    public MainWindow(Client client){        setLayout(new BorderLayout());        panelAreaButton = new JPanel(new BorderLayout());        glownaArea = new JTextArea();        glownaArea.setEditable(false);        glownaArea.setBackground(Color.PINK);        setTitle(client.getNick());        malaArea = new JTextArea();        buttonWysli = new JButton("wysli");        buttonWysli.addActionListener(e -> {            String message = malaArea.getText();            System.out.println("Wysylam: " + message + " z " + client.getNick());            if(malaArea.getText().length()>0)                client.sendMessage(message);            malaArea.setText("");        });        panelAreaButton.add(malaArea, BorderLayout.CENTER);        panelAreaButton.add(buttonWysli, BorderLayout.EAST);        add(glownaArea, BorderLayout.CENTER);        add(panelAreaButton, BorderLayout.SOUTH);        setDefaultCloseOperation(EXIT_ON_CLOSE);        setPreferredSize(new Dimension(350,350));        pack();        setVisible(true);    }    public void writeText(String message){        glownaArea.append(message + "\n");        glownaArea.setCaretPosition(glownaArea.getDocument().getLength());        glownaArea.update(glownaArea.getGraphics());    }}